mapper.insert:


		    select nvl(max(seq),0)+1 as seq
   			from tb_guestbook

		insert into tb_guestbook
		(
			seq
			,title
			,writer
			,contents
			,wdate
			,hit
		)
		values
		(
			#{seq}
			,#{title}
			,#{writer}
			,#{contents}
			,sysdate
			,0
		)

mapper.update:
update tb_guestbook
	set title=#{title}
	,contents=#{contents}
where seq=#{seq}
